CREATE TABLE IF NOT EXISTS foods (id int(11) NOT NULL AUTO_INCREMENT, name varchar(100) NOT NULL, category varchar(50) NOT NULL, price decimal(10,2) NOT NULL, PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=latin1; INSERT INTO foods (name, category, price) VALUES ('Chicken Fried Rice', 'Main Course', 1200.00), ('Vegetable Kottu', 'Main Course', 900.00), ('Egg Hoppers', 'Breakfast', 350.00), ('Fruit Platter', 'Breakfast', 500.00), ('Fish Curry', 'Main Course', 1300.00), ('Chocolate Cake', 'Dessert', 600.00), ('Ice Cream', 'Dessert', 400.00), ('Sri Lankan Tea', 'Beverage', 250.00), ('Coffee', 'Beverage', 300.00), ('Fresh Juice', 'Beverage', 350.00); CREATE TABLE IF NOT EXISTS food_orders (id int(11) NOT NULL AUTO_INCREMENT, email varchar(100) NOT NULL, food_id int(11) NOT NULL, quantity int(11) NOT NULL, order_time datetime NOT NULL, status varchar(20) DEFAULT 'pending', bill_amount decimal(10,2) NOT NULL, PRIMARY KEY (id), FOREIGN KEY (food_id) REFERENCES foods(id)) ENGINE=InnoDB DEFAULT CHARSET=latin1;
